package com.adote.repo; import org.springframework.data.jpa.repository.*; import com.adote.domain.Animal; import com.adote.domain.User; import java.util.*; public interface AnimalRepository extends JpaRepository<Animal,String>{ List<Animal> findByAvailableTrue(); List<Animal> findByAvailableTrueAndGenderIgnoreCase(String gender); List<Animal> findByAvailableTrueAndTypeIgnoreCase(String type); List<Animal> findByOwner(User owner);}